definition.script:
pipeline 
{
    agent 
    {
        label 'docker'
    }
    environment 
    {
        DOCKERHUB_CREDENTIALS=credentials('docker-hub')
    }
    stages 
    {
        stage('Clone') 
        {
            steps 
            {
                git branch: 'main', url: 'http://192.168.99.202:3000/vagrant/exam'
            }
        }
        stage('Build images')
        {
            steps
            {
                sh 'docker compose -f docker-compose.yml up -d --build'
            }
        }
        stage('Test')
        {
            steps
            {
                script 
                {
                    echo 'Wait 10 seconds'
                    sh 'sleep 10'

                    echo 'Test #1 - reachability'
                    sh 'echo $(curl --write-out "%{http_code}" --silent --output /dev/null http://localhost:8080) | grep 200'

                    echo 'Wait 10 seconds'
                    sh 'sleep 10'

                    echo 'Test #2'
                    sh 'echo $(curl --silent http://localhost:8080) | grep $(cat /home/vagrant/workspace/exam/client/code/app.dat)'
                }
            }
        }
        stage('CleanUp')
        {
            steps
            {
                sh 'docker compose -f docker-compose.yml down'
            }
        }
        stage('Login')
        {
            steps 
            {
                sh 'echo $DOCKERHUB_CREDENTIALS_PSW | docker login -u $DOCKERHUB_CREDENTIALS_USR --password-stdin'
            }
        }
        stage('Image Build and Push') 
        {
            steps 
            {
                sh '''
                    docker image rm -f $DOCKERHUB_CREDENTIALS_USR/client || true
                    docker image rm -f $DOCKERHUB_CREDENTIALS_USR/storage || true
                    docker image rm -f $DOCKERHUB_CREDENTIALS_USR/generator || true
                    cd /home/vagrant/workspace/exam/client && docker image build -t $DOCKERHUB_CREDENTIALS_USR/client .
                    cd /home/vagrant/workspace/exam/storage && docker image build -t $DOCKERHUB_CREDENTIALS_USR/storage .
                    cd /home/vagrant/workspace/exam/generator && docker image build -t $DOCKERHUB_CREDENTIALS_USR/generator .
                    docker image push $DOCKERHUB_CREDENTIALS_USR/client
                    docker image push $DOCKERHUB_CREDENTIALS_USR/storage
                    docker image push $DOCKERHUB_CREDENTIALS_USR/generator
                '''
            }
        }
        stage('Deploy')
        {
            steps
            {
                 sh 'docker compose -f docker-compose-deploy.yml down'
                 sh 'docker compose -f docker-compose-deploy.yml up -d'
            }
        }
    }
    post 
    { 
        always 
        { 
            cleanWs()
        }
    }
}
